import { Grid, Typography } from '@material-ui/core';
import React, { useState } from 'react';
import DeleteIcon from '@material-ui/icons/Delete';
import CheckCircleIcon from '@material-ui/icons/CheckCircle';
import CheckCircleOutlineIcon from '@material-ui/icons/CheckCircleOutline';

function EachNode(props) {
	const [finished, setFinished] = useState(false);

	return (
		<Grid direction="row" container className="each-to-do">
			<Grid xs="3" align="end" className="each-to-do-icon">
				{' '}
				{finished ? (
					<CheckCircleIcon onClick={(e) => setFinished(true)}></CheckCircleIcon>
				) : (
					setFinishIcon(
						<CheckCircleOutlineIcon
							onClick={(e) => setFinished(false)}
						></CheckCircleOutlineIcon>
					)
				)}
			</Grid>
			<Grid xs="6">
				{' '}
				<Typography
					align="center"
					style={{ textDecoration: finished ? 'line-through' : 'none' }}
				>
					{props.toDo}
				</Typography>
			</Grid>
			<Grid xs="3" align="start" className="each-to-do-icon">
				{' '}
				<DeleteIcon
					onClick={(e) => {
						props.delete(props.index);
					}}
				></DeleteIcon>
			</Grid>
		</Grid>
	);
}

export default EachNode;
